import React, { useState } from 'react';
import { View, TextInput, Button, StyleSheet } from 'react-native';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { auth } from '../database/firebaseConfig'; // Asegúrate de importar correctamente auth si lo estás utilizando.
import { db } from '../database/firebaseConfig';
import { collection, addDoc } from '@firebase/firestore';

const SignUp = () => {
  const [name, setName] = useState('');
  const [surname, setSurname] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSignUp = async () => {
    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;
  
      // Almacenar 'name', 'surname', 'email' y 'uid' en Realtime Database
      const userRef = ref(db, 'users/' + user.uid);
      await set(userRef, {
        name: name,
        surname: surname,
        email: email,
        uid: user.uid
      });
  
      console.log("User data stored in Realtime Database");
  
      // TODO: Redirige al usuario a la pantalla principal después de registrarse.
  
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <View style={styles.container}>
      <TextInput placeholder="Nombre" value={name} onChangeText={setName} style={styles.input} />
      <TextInput placeholder="Apellido" value={surname} onChangeText={setSurname} style={styles.input} />
      <TextInput placeholder="Correo" value={email} onChangeText={setEmail} style={styles.input} />
      <TextInput
        placeholder="Contraseña"
        value={password}
        onChangeText={setPassword}
        secureTextEntry
        style={styles.input}
      />
      <Button title="Registrarse" onPress={handleSignUp} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    padding: 20,
  },
  input: {
    borderWidth: 1,
    borderColor: '#ccc',
    padding: 10,
    marginBottom: 10,
    borderRadius: 5,
  },
});

export default SignUp;
