import React from 'react';
import { View, Button, TextInput, ScrollView, StyleSheet } from 'react-native';
import { getDatabase, ref, set } from 'firebase/database';
import { useNavigation } from '@react-navigation/native';
import { db } from '../database/firebaseConfig';


const MisCasos = () => {
    const navigation = useNavigation();
    const [showDetails, setShowDetails] = useState(false);

    const toggleDetails = () => {
      setShowDetails(!showDetails);
    };

    return (
      <View>
        <Text>Mis Casos</Text>
        <Button title="Divorcio, Juan Pérez" onPress={() => toggleDetails()} />
        {showDetails ? (
          <View style={styles.details}>
            <Text>Descripción del caso: Divorcio por diferencias irreconciliables, ambos de acuerdo con el divorcio.</Text>
            <Text>Fecha: 25 de septiembre de 2023.</Text>
          </View>
        ) : null}
      </View>
    );
  };

  const styles = StyleSheet.create({
    details: {
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      opacity: 0,
      transition: 'all 300ms ease-in-out',
    },
  });

  export default MisCasos;
