import React, { useEffect, useState } from 'react';
import { View, Button, TextInput, ScrollView, StyleSheet } from 'react-native';
import { getFirestore, collection, query, onSnapshot } from 'firebase/firestore';
import { useNavigation } from '@react-navigation/native';
import { ListItem, Avatar } from 'react-native-elements';

import { db } from '../database/firebaseConfig';

const MisClientes = () => {
  const navigation = useNavigation();
  const [usuarios, setUsuarios] = useState([]);

  useEffect(() => {
    const unsubscribe = onSnapshot(query(collection(db, 'clientes')), (querySnapshot) => {
      const usuarios = [];

      querySnapshot.docs.forEach((doc) => {
        const { nombre, email, telefono, observaciones } = doc.data();
        usuarios.push({
          id: doc.id,
          nombre,
          email,
          telefono,
          observaciones,
        });
      });

      setUsuarios(usuarios);
    });

    return () => unsubscribe();
  }, []);

  return (
    <ScrollView>
      {usuarios.map((usuario) => (
        <ListItem key={usuario.id}>
          <Avatar>{usuario.nombre[0]}</Avatar>
          <ListItem.Content>
            <ListItem.Title>{usuario.nombre}</ListItem.Title>
            <ListItem.Subtitle>{usuario.email}</ListItem.Subtitle>
          </ListItem.Content>
        </ListItem>
      ))}
    </ScrollView>
  );
};

export default MisClientes;
