import React, { useState } from 'react';
import { View, Button, TextInput, ScrollView, StyleSheet } from 'react-native';
import { getDatabase, ref, set } from 'firebase/database';
import { useNavigation } from '@react-navigation/native';

import { db } from '../database/firebaseConfig';


const MisCasos = () => {
    const navigation = useNavigation();
    const [showDescription, setShowDescription] = useState(false);

    const toggleDescription = () => {
      setShowDescription(!showDescription);
    };

    return (
      <View>
        <Text>Mis Casos</Text>
        <Button title="Divorcio, Juan Perez" onPress={() => toggleDescription()} />
        {showDescription && (
          <View style={styles.description}>
            <Text>Descripción del caso:</Text>
            <Text>Divorcio por diferencias irreconciliables, ambos de acuerdo con el divorcio.</Text>
            <Text>Fecha: 25 de septiembre de 2023.</Text>
          </View>
        )}
        <Text>Pensión Alimenticia, Juan Lopez</Text>
      </View>
    );
  };

  const styles = StyleSheet.create({
    description: {
      borderWidth: 1,
      borderColor: '#ccc',
      borderRadius: 5,
      padding: 10,
      marginTop: 10,
    },
  });

  export default MisCasos;
