import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, ScrollView } from 'react-native';
import { getDatabase, ref, onValue, off } from 'firebase/database';

const MisCasos = () => {
  const [casos, setCasos] = useState([]);

  useEffect(() => {
    const dbRef = ref(getDatabase(), 'casos/');
    const handleData = snapshot => {
      const data = snapshot.val();
      const listItems = [];
      for (let id in data) {
        listItems.push(data[id]);
      }
      setCasos(listItems);
    };

    onValue(dbRef, handleData, error => {
      console.error(`Failed to fetch cases: ${error}`);
    });

    return () => {
      off(dbRef, 'value', handleData);
    };
  }, []);

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Mis Casos</Text>
      <ScrollView>
        {casos.map((caso, index) => (
          <View key={index} style={styles.case}>
            <Text style={styles.caseTitle}>{caso.Causa}, {caso.Cliente}</Text>
            <Text style={styles.caseDescription}>Descripci√≥n del caso:</Text>
            <Text style={styles.caseDescription}>{caso.Descripcion}</Text>
            <Text style={styles.caseDescription}>Fecha: {caso.Fecha}.</Text>
          </View>
        ))}
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  case: {
    marginTop: 10,
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    padding: 10,
  },
  caseTitle: {
    fontWeight: 'bold',
    marginBottom: 5,
  },
  caseDescription: {
    marginBottom: 5,
  },
});

export default MisCasos;
