import React, { useState, useEffect } from 'react';
import { View, Button, TextInput, ScrollView, StyleSheet } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { Card } from 'react-native-elements';

const Home = () => {
  const navigation = useNavigation();

  const [clientes, setClientes] = useState([]);

  useEffect(() => {
    // Obtener la referencia a la base de datos de clientes
    const clientesRef = db.ref('abogados/clientes');

    // Escuchar los cambios en la base de datos
    clientesRef.on('value', (snapshot) => {
      // Actualizar el estado de clientes con los datos de la base de datos
      setClientes(snapshot.val());
    });
  }, []);

  return (
    <ScrollView>
      <View>
        <Card title='Mis Casos' onPress={() => navigation.navigate('MisCasos')}>
          <Text>Ver todos tus casos</Text>
        </Card>
      </View>
      <View>
        <Card title='Reuniones' onPress={() => navigation.navigate('Reuniones')}>
          <Text>Ver todas tus reuniones</Text>
        </Card>
      </View>
      <View>
        <Card title='Mis eventos' onPress={() => navigation2.navigate('MisEventos')}>
          <Text>Ver todos tus eventos</Text>
        </Card>
      </View>
      <View>
        <Card title='Mis Clientes' onPress={() => navigation.navigate('Clientes')}>
          <Text>Ver todos tus clientes</Text>
        </Card>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  button: {
    backgroundColor: '#0000FF',
    borderRadius: 10,
    padding: 10,
  },
  buttonText: {
    color: '#FFFFFF',
    fontSize: 16,
  },
});

export default Home;
