import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { getDatabase, ref, onValue } from 'firebase/database';

import { db } from '../database/firebaseConfig';

const MisCasos = () => {
  const navigation = useNavigation();
  const [casos, setCasos] = useState([]);

  useEffect(() => {
    const casosRef = ref(db, 'casos');
    const unsubscribe = onValue(casosRef, (snapshot) => {
      const data = snapshot.val();
      const listaCasos = [];
      for(let id in data){
        listaCasos.push({ id, ...data[id] });
      }
      setCasos(listaCasos);
    });

    return () => {
      unsubscribe();
    };
  }, []);

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Mis Casos</Text>
      {casos.map(caso => (
        <View key={caso.id} style={styles.case}>
          <Text style={styles.caseTitle}>{caso.nombreCaso}, {caso.nombreCliente}</Text>
          <Text style={styles.caseDescription}>Descripci√≥n del caso:</Text>
          <Text style={styles.caseDescription}>{caso.descripcionCaso}</Text>
          <Text style={styles.caseDescription}>Fecha: {caso.fechaCaso}</Text>
        </View>
      ))}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  case: {
    marginTop: 10,
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    padding: 10,
  },
  caseTitle: {
    fontWeight: 'bold',
    marginBottom: 5,
  },
  caseDescription: {
    marginBottom: 5,
  },
});

export default MisCasos;
