import React, { useState } from 'react';
import { View, Text, Button, TextInput, StyleSheet } from 'react-native';
import { Calendar } from 'react-native-calendars';
import Modal from 'react-native-modal';

const Eventos = () => {
  const [selectedDate, setSelectedDate] = useState('');
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [eventName, setEventName] = useState('');
  const [eventImportance, setEventImportance] = useState('');
  const [eventDescription, setEventDescription] = useState('');

  const toggleModal = () => {
    setIsModalVisible(!isModalVisible);
  };

  const handleDateSelect = (day) => {
    setSelectedDate(day.dateString);
  };

  const handleEventSchedule = () => {
    toggleModal();
  };

  return (
    <View style={styles.container}>
      <Text style={styles.heading}>Mis Eventos</Text>
      <Calendar onDayPress={handleDateSelect} />

      {selectedDate && (
        <View style={styles.selectedDateContainer}>
          <Text>Fecha seleccionada: {selectedDate}</Text>
          <Button title="Programar Evento" onPress={handleEventSchedule} />
        </View>
      )}

      <Modal isVisible={isModalVisible}>
        <View style={styles.modalContainer}>
          <Text style={styles.modalHeading}>Programar Evento</Text>
          <TextInput
            placeholder="Nombre del Evento"
            onChangeText={(value) => setEventName(value)}
          />
          <TextInput
            placeholder="Grado de Importancia"
            onChangeText={(value) => setEventImportance(value)}
          />
          <TextInput
            placeholder="DescripciÃ³n"
            onChangeText={(value) => setEventDescription(value)}
          />
          <Button title="Guardar Evento" onPress={toggleModal} />
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    paddingTop: 20,
  },
  heading: {
    fontSize: 24,
    marginBottom: 20,
  },
  selectedDateContainer: {
    marginTop: 20,
    alignItems: 'center',
  },
  modalContainer: {
    backgroundColor: 'white',
    padding: 20,
    borderRadius: 10,
  },
  modalHeading: {
    fontSize: 18,
    marginBottom: 10,
  },
});

export default Eventos;
